$(document).ready(function(){$("form.add_product").submit(function(){var a=[];$(this).find(".option").each(function(c,d){a[c]=$(d).val()});if(a.length===0){var b={product_id:$(this).find("input[name=product_id]").val()}}else{var b={product_id:$(this).find("input[name=product_id]").val(),"option[]":a}}$("div.loading").block({message:"Processing",css:{border:"none",padding:"15px",backgroundColor:"#000","-webkit-border-radius":"10px","-moz-border-radius":"10px","border-radius":"8px","font-weight":"bold",opacity:0.8,color:"#FFF"}});$.ajax({type:"POST",url:"cart.php?action=add_product",data:b,success:function(){$.get("cart.php?action=get_cart",function(c){$("#cart_items").fadeOut("slow").html(c).fadeIn("slow")});$("div.loading").unblock();$.get("cart.php?action=show_errors",function(c){$("#show_errors").fadeOut("slow").html(c).fadeIn("slow")})}});return false});$(".table tr:nth-child(odd)").addClass("odd");$(".table tr:nth-child(even)").addClass("even")});